	for(int i=num-1;i>=0;i--)//倒序的for循环 
	{
		ans[j]=b[i];//倒序存储 
		j++;//数组长度加一 
	}
	for(int i=0;i<num;i++)
	{
		b[i]=ans[i];//重新导出 
	}
}
void And(int a)
{
	b[num]=a;//赋值a 
	num++;//将位数加一 
}
int main()
{
	int n;
	cin>>n;
	int a;
	for(int i=0;i<n;i++)
	{
		scanf("%d",&a);//输入a 
		And(a);//把a接到b后面 
		fanzhuan();//翻转b数组 
	}
	for(int i=0;i<n;i++)
