	for (int i=1;i<=cntr;i++){
		ae(i+cntl,T,1);
	}
	for (int i=1;i<=cntl;i++){
		for (int j=1;j<=cntr;j++){
			if (!ckp[abs(L[i]-R[j])]){
				ae(i,j+cntl,1);
			}
