```cpp
int n,m,e[N];
int tr[N<<2],tag[N<<2];
char s[N];
vector<int> vec;
inline void pushup(int o){
	tr[o]=tr[o<<1]|tr[o<<1|1];
}
inline void pushdown(int l,int r,int o){
	if(tag[o]==-1){
		return;
	}
	tr[o<<1]=tag[o],tr[o<<1|1]=tag[o];
	tag[o<<1]=tag[o],tag[o<<1|1]=tag[o];
	tag[o]=-1;
}
void build(int l,int r,int o){
	tag[o]=-1;
	if(l==r){
		tr[o]=e[l];
		return;
	}
	int mid=(l+r)>>1;
	build(l,mid,o<<1);
	build(mid+1,r,o<<1|1);
	pushup(o);
}
int query_l(int l,int r,int o){
	if(l==r){
		if(tr[o]){
			return l;
		}
		return -1;
	}
	pushdown(l,r,o);
	int mid=(l+r)>>1;
	if(tr[o<<1]){
		return query_l(l,mid,o<<1);
	}
	return query_l(mid+1,r,o<<1|1);
}
int query_r(int l,int r,int o){
	if(l==r){
		if(tr[o]){
			return l;
		}
		return -1;
	}
	pushdown(l,r,o);
	int mid=(l+r)>>1;
	if(tr[o<<1|1]){
		return query_r(mid+1,r,o<<1|1);
	}
	return query_r(l,mid,o<<1);
}
void update(int l,int r,int o,int x,int y,int k){
	if(l>=x&&r<=y){
		tr[o]=k;
		tag[o]=k;
		return;
	}
	pushdown(l,r,o);
	int mid=(l+r)>>1;
	if(x<=mid){
		update(l,mid,o<<1,x,y,k);
	}
	if(y>mid){
		update(mid+1,r,o<<1|1,x,y,k);
	}
	pushup(o);
}
void solve(){
	scanf("%d%s",&n,s+1);
	m=strlen(s+1);
	for(int i=1;i<=n-m;i++){
		e[i]=0;
	}
	for(int i=1;i<=m;i++){
		e[i+n-m]=s[i]-'0';
	}
	build(1,n,1);
	vec.push_back(1);
	for(int i=2;i<=n;i++){
		int x=query_l(1,n,1),y=query_r(1,n,1);//线段树上二分找左/右第一个1
		if(x==y&&y==n){
			break;
		}//当前要求排名为1，结束
		if(x<i&&x!=y){
			vec.push_back(1);
			update(1,n,1,x,x,0);
		}else{
			vec.push_back(0);
			if(y==n){
				update(1,n,1,n,n,0);
			}else{
				update(1,n,1,y,y,0);
				update(1,n,1,y+1,n,1);
			}
		}
	}
	for(int i:vec){
		printf("%d",i);
	}
}
signed main(){
	int t=1;
	//	scanf("%d",&t);
	while(t--)solve();
}
