**My Solution:**

我的实现就是先将 $A_i,B_i$ 离散化，每次在线段树上更新区间 $+1/-1$，维护区间和、区间最小值即可，找的时候在线段树上二分出第一个 $<0$ 的位置，其他实现相同，但貌似难写一些？

```cpp
#include<bits/stdc++.h>
#define int long long
using namespace std;
const int maxn=3e5+10;
int n,a[maxn],b[maxn];
vector<int> v;
priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> pq;
priority_queue<pair<int,int>> ok;
struct node{
	int l,r;
	int val,mn,tag;
}tree[maxn<<3];
void pushup(int x){
	tree[x].val=tree[x<<1].val+tree[x<<1|1].val;
	tree[x].mn=min(tree[x<<1].mn,tree[x<<1|1].mn);
}
void pushdown(int x){
	if(!tree[x].tag){
		return;
	}
	tree[x<<1].tag+=tree[x].tag;
	tree[x<<1|1].tag+=tree[x].tag;
	tree[x<<1].val+=tree[x].tag*(tree[x<<1].r-tree[x<<1].l+1);
	tree[x<<1|1].val+=tree[x].tag*(tree[x<<1|1].r-tree[x<<1|1].l+1);
	tree[x<<1].mn+=tree[x].tag;
	tree[x<<1|1].mn+=tree[x].tag;
	tree[x].tag=0;
}
void build(int x,int l,int r){
	tree[x].l=l;
	tree[x].r=r;
	tree[x].val=tree[x].mn=tree[x].tag=0;
	if(l==r){
		return;
	}
	int mid=(l+r)>>1;
	build(x<<1,l,mid);
	build(x<<1|1,mid+1,r);
}
void update(int x,int l,int r,int v){
	if(tree[x].l>=l&&tree[x].r<=r){
		tree[x].tag+=v;
		tree[x].val+=v*(tree[x].r-tree[x].l+1);
		tree[x].mn+=v;
		return;	
	}
	pushdown(x);
	int mid=(tree[x].l+tree[x].r)>>1;
	if(l<=mid){
		update(x<<1,l,r,v);
	}
	if(r>mid){
		update(x<<1|1,l,r,v);
	}
	pushup(x);
}
int find(int x){
	if(tree[x].l==tree[x].r){
		return tree[x].l;
	}
	pushdown(x);
	if(tree[x<<1].mn<0){
		return find(x<<1);
	}
	if(tree[x<<1|1].mn<0){
		return find(x<<1|1);
	}
	return -1;
}
signed main(){
	cin>>n;
	for(int i=1;i<=n;i++){
		cin>>a[i]>>b[i];
		v.push_back(a[i]);
		v.push_back(b[i]);
	}
	sort(v.begin(),v.end());
	v.resize(unique(v.begin(),v.end())-v.begin());
	int m=(int)v.size();
	build(1,1,m);
	int ans=n;
	for(int i=1;i<=n;i++){
		a[i]=lower_bound(v.begin(),v.end(),a[i])-v.begin()+1;
		b[i]=lower_bound(v.begin(),v.end(),b[i])-v.begin()+1;
		if(a[i]<b[i]){
			ans--;
			update(1,a[i],m,-1);
			update(1,b[i],m,1);
		}
		else{
			pq.push(make_pair(b[i],a[i]));
		}
	}
	int t=find(1);
	while(t!=-1){
		while(!pq.empty()&&pq.top().first<=t){
			ok.push(make_pair(pq.top().second,pq.top().first));
			pq.pop();
		}
		int tmp=t;
		while(!ok.empty()&&t==tmp){
			ans--;
			update(1,ok.top().first,m,-1);
			update(1,ok.top().second,m,1);
			ok.pop();
			t=find(1);
		}
		if(t==tmp){
			cout<<-1<<endl;
			return 0;
		}
	}
	cout<<ans<<endl;
	return 0;
}
```

