### 代码
```cpp
#include<bits/stdc++.h>
#define int long long
using namespace std;
inline int R(){
	int x=0,f=1;int ch=getchar();
	while(!isdigit(ch)){if(ch=='-')f=-1;ch=getchar();}
	while(isdigit(ch)){x=x*10+ch-48;ch=getchar();}return x*f;
}
inline void write(int x){
	if(x<0){x=-x;putchar('-');}
	int y=0;char z[70];
	while(x||!y){z[y++]=x%10+48;x/=10;}
	while(y--)putchar(z[y]);
}
#define rep(a,b,c) for(int a=b;a<=c;a++)
const int maxn=2e5+5;
int n,a[maxn],b[maxn],mi[20];
int calc(int x){
	int res=0;
	while(x){
		res+=x%10;
		x/=10;
	}
	return res;
}
int ck(int bs,int x){
	int l=1,r=n,ret=n+1;
	while(l<=r){
		int mid=l+r>>1;
		if(b[x]+b[mid]>=mi[bs]) r=mid-1,ret=mid;
		else l=mid+1;
	}
	return n-ret+1;
}
signed main(){
	n=R();rep(i,1,n)a[i]=R();
	mi[0]=1;rep(i,1,15)mi[i]=mi[i-1]*10;
	int ans=0;rep(i,1,n)ans+=calc(a[i]);ans*=2*n;
	rep(j,1,15){
		rep(i,1,n)b[i]=a[i]%mi[j];
		sort(b+1,b+n+1);
		rep(i,1,n)ans-=ck(j,i)*9;
	}
	write(ans);
}

```

