    inline int read()//快读，小伙伴们可以了解一下
    {
  	  int p=0,f=1;char c=getchar();
  	  while(c<'0'||c>'9'){if(c=='-')f=-1;c=getchar();}
  	  while(c>='0'&&c<='9'){p=p*10+c-'0';c=getchar();}
  	  return f*p;}
    int a,b;
    int abs_(int k)//绝对值函数
    {return k>=0?k:-k;}
    int main()
    {
        a=read(),b=read();
        printf("%d",abs_(a)+abs_(b-a)+abs(b));
        //直接计算输出答案即可
  	  return 0;
    }

