    int a[1000000],b[1000000];//定义变量
    int main()
    {
    cin>>n>>m;//读入
    for(int i=1; i<=n; i++)
    {
        cin>>a[i];
        b[a[i]]++;//这个数出现的次数++
    } 
    for(int i=0; i<=m; i++)//从0找到m
    {
        if(b[i]>da)da=b[i],d=i;//找最大
    }
    if(da>n/2)cout<<d<<endl;//如果da超过半数，输出那个数
    else cout<<"?"<<endl;//否则输出问号
    return 0;
    }

