{
	int n，m；
	scanf("%d%d",&n,&m)；
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=m;j++)
		{
			scanf("%s",s)；//一个字符串
			if(s[0]=='s'&&s[1]=='n'&&s[2]=='u'&&s[3]=='k'&&s[4]=='e')//其实可以用cmp比较，但是只有五个字符，蒟蒻就一个一个比了。。。
			{
				printf("%c%d\n",'A'+j-1,i);//把列数转化为大写字母，行数直接打印

			}
		}
	}
	return 0；
} //友情提示：内有防抄袭bug
