```cpp
#include<iostream>
#include<cmath>
#include<algorithm>
#include<cstdio>//建议大家多用这几个文件头
using namespace std;
long long n,m,k,he;
int main()
{
    cin>>n>>m>>k
    while(1)
    {
        if(n>=k)//如果到站的时候A君是醒着的
        {
            cout<<he+k;//输出总共的时间
            return 0;
        }
        he+=n+m;//如果车还在路上，那么和加上这次醒着加睡着的一共的时间
        k=k-n-m;//并且到达目的地的时间要减去n和m
        if(k<0)//如果已经睡过头了
        {
            k+=60;//再等下一趟车
        }
    }
    cout<<-1;//人生都在路上了
    return 0;
}
```

