```cpp
#include <bits/stdc++.h>
using namespace std;
int n,a,b;
bool f[17][17];
int w(int x,int y){
	if(x<0 || x>a || y<0 || y>b){
		return 0;
	}
	if(f[x][y]){
		return 0;
	}
	if(x==1 && y==1){
		return 1;
	}
	return w(x,y-1)+w(x-1,y);
}
int main(){
	cin>>a>>b;
	cin>>n;
	for(int i=1;i<=n;i++){
		int t1,t2;
		cin>>t1>>t2;
		f[t1][t2]=1;
	}
	cout<<w(a,b)<<endl;
	return 0;
}

```
