}a[20005],b[20005];
long long x[20005],y[20005],n,m;
struct rule{
	bool operator()(const xyq &s1,const xyq &s2){ //自定义排序函数。此函数表示先按x排序,x一样再按y排序。
		return s1.x<s2.x||s1.x==s2.x&&s1.y<s2.y;
	}
};
