int main(){
	long long i;
	cin>>n;
	for(i=0;i<n;++i){
		cin>>a[i].x>>a[i].y;
	}
	cin>>m;
	for(i=0;i<m;++i){
		cin>>b[i].x>>b[i].y;
	}
	sort(a,a+n,rule());
	sort(b,b+m,rule());
	for(i=0;i<n;++i){
		x[i]=a[i].x-a[0].x; //计算偏离值。
		y[i]=a[i].y-a[0].y;
	}
	for(i=0;i<m;++i){
		if(check(i)){
			break;
		}
	}
	cout<<b[i].x-a[0].x<<" "<<b[i].y-a[0].y<<endl;
	return 0;
}
