## 代码

```cpp
#include <bits/stdc++.h>
using namespace std;
const int N = 1e3 + 10;
int n, m, k;
int cnt, sum;
int dx[4] = {0, 1, 0, -1};  //四个方向
int dy[4] = {1, 0, -1, 0};
char a[N][N];
bool vis[N][N];
struct F {
  int w;
  int x, y;
} f[N];
struct Node {
  int x, y, s;
};
bool cmp(F A, F B) {  //按值排序
  return A.w < B.w;
}
int bfs(int x, int y, int s, int fx, int fy) {  //一个普普通通的广搜
  queue<Node> q;
  Node cur = {x, y, s};
  q.push(cur);
  memset(vis, 0, sizeof(vis));
  vis[x][y] = 1;  //记得标记起点
  while (!q.empty()) {
    Node cur = q.front();
    q.pop();
    if (cur.x == fx && cur.y == fy) {  //到达终点
      return cur.s;
    }
    for (int i = 0; i <= 3; i++) {
      int nx = cur.x + dx[i];
      int ny = cur.y + dy[i];
      if (nx >= 1 && nx <= n && ny >= 1 && ny <= m && !vis[nx][ny] && a[nx][ny] != 'X') {  //判断可不可以走
        vis[nx][ny] = 1;
        Node ne = {nx, ny, cur.s + 1};
        q.push(ne);
      }
    }
  }
}
int main() {
  cin >> n >> m >> k;
  for (int i = 1; i <= n; i++) {
    for (int j = 1; j <= m; j++) {
      cin >> a[i][j];
      if (a[i][j] == 'S') {  //起点特殊处理
        cnt++;
        f[cnt].w = 0;
        f[cnt].x = i, f[cnt].y = j;
      }
      if (a[i][j] >= '1' && a[i][j] <= '9') {  //存储数字的值与下标
        cnt++;
        f[cnt].w = a[i][j] - '0';
        f[cnt].x = i, f[cnt].y = j;
      }
    }
  }
  sort(f + 1, f + cnt + 1, cmp);
  for (int i = 1; i < cnt; i++) {
    sum += bfs(f[i].x, f[i].y, 0, f[i + 1].x, f[i + 1].y);  //累加相邻的最小时间
  }
  cout << sum;
  return 0;
}
```


