{
    cin>>b;
    c=b;
    cin>>b;
    c=min(b,c);
    cin>>b;
    c=min(b,c);
    cin>>b;
    d=b;
    cin>>b;
    d=min(b,d);
    cout<<c+d-50<<endl;
