{
	cin>>a>>b>>c>>n;//输入
	(n*1-a>=0)?cout<<n*1-a<<" ":cout<<0<<" ";//三目运算符
	(n*2-b>=0)?cout<<n*2-b<<" ":cout<<0<<" ";//三目运算符
