#include<cstdio>
#include<cctype>
#include<algorithm>
int n,k,a[55],ans,cc;
inline long long read()//快排那么香为什么没人用
{    
    long long a=0;
    int f=0;
    char p=getchar();
    while(!isdigit(p))
    {
        f|=p=='-';
        p=getchar();
    }
    while(isdigit(p))
    {
        a=(a<<3)+(a<<1)+(p^48);
        p=getchar();
    }
    return f?-a:a;
}
int main()
{
	n=read();k=read();
	for(int i=1;i<=n;i++) a[i]=read();
	std::sort(a+1,a+n+1);//快排
	for(int i=n;i>0;i--)
        {
               ans+=a[i];
	           cc++;
               if(ans>=k)
               {
                   printf("%d\n",cc);//记得\n！！！
                   return 0;
               }
        }
        printf("%d\n",-1);//记得\n！！！
        return 0;//好习惯
