	for(int i=0;i<str.size();i++){
	//判断
    if((str[i]=='a'&&str[i+1]=='x'&&str[i+2]=='a')||
        (str[i]=='e'&&str[i+1]=='x'&&str[i+2]=='e')||
        (str[i]=='i'&&str[i+1]=='x'&&str[i+2]=='i')||
        (str[i]=='o'&&str[i+1]=='x'&&str[i+2]=='o')||
        (str[i]=='u'&&str[i+1]=='x'&&str[i+2]=='u'))
        str[i]=str[i+1]=str[i+2]='.';//修改
	}
