#include<iostream>
#include<algorithm>
using namespace std;
const int maxn=1000000005;
int n,m,x,y;
bool a[maxn],b[maxn];
int main()
{
    cin>>n>>m;
    for(int i=1;i<=n;i++)
    {
        cin>>x;
        a[x]=1;
    }
    for(int i=1;i<=m;i++)
    {
        cin>>y;
        b[y]=1;
    }
    for(int i=1;i<=maxn;i++)
    { 
        if(a[i]&&b[i]) cout<<i<<" ";
    }
    return 0;
}
```
