#include<bits/stdc++.h>
using namespace std;

int a[1005],b[1005];

int main()
{
    int n,m;
    cin >> n >> m;
    for(int i = 1;i <= n;i ++)
    {
        cin >> a[i];
    }
    for(int i = 1;i <= m;i ++)
    {
        cin >> b[i];
    }
    sort(a + 1,a + n + 1);
    sort(b + 1,b + m + 1);
    for(int i = 1;i <= n;i ++)
    {
        for(int j = 1;j <= m;j ++)
        {
            if(a[i] == b[j])
            {
                cout << a[i] << ' ';
            }
        }
    }
}
