using namespace std;
double r1,r2;
int read() {
	int x=0,f=1;
	char ch;
	while(ch<'0'||ch>'9')  {
		if(ch=='-')f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9') {
		x=x*10+ch-'0';
		ch=getchar();
	}
	return f*x;
}
int main() {

	r1=read();
	r2=read();
	printf("%.10lf\n",r1*r2/(r1+r2));

	return 0;
}
```

