#include<bits/stdc++.h>//头文件不多说
using namespace std;
long long a,b=2;//一定要用long long,否则在#31-#41会WA
int main(){
    cin>>a;
    for(int i=0;i<a;i++)b=(b+1)*2;//乘法分配律
    cout<<b<<endl;
}
```

