#include<iostream>//一坨头文件
#include<cstdio>
using namespace std;
int n;
long long f[400];//一定要开long long，不然有点会WA!
int main()
{
    cin>>n;
    f[0]=2;//给第一个数组赋2
    for(int i=0;i<=n;i++)
    {
    f[i]=f[i-1]*2+2;//递推算式
    }
