using namespace std;
long long n,ans=2;//初始化一下
int main()
{
   cin>>n;
   for(int i=1;i<=n;i++）ans=ans*2+2;//变量(无数组)递推
   cout<<ans<<"\n";//cyy等于288___why提醒您:AT不换行，爆零两行泪
