#include <bits/stdc++.h>
using namespace std;
int main()
{
    char s1[11],s2[11];
    scanf("%s%s",s1,s2);
    if(s1[0]=='S'&&s2[0]=='H')cout<<"YES"<<endl;//直接判断首字母分别是不是S 和 H。
    else cout<<"NO"<<endl;
}
```
