    using namespace std;
    int n,djy=1,a,b,c,d,e,g;//定义变量
    int main()
    {
    cin>>n;//读入n
    while(djy<=n)//有n道题目，运行n遍（为了跟其他大佬区分，用while）
    {
        cin>>a>>b>>c>>d>>e;//读入五个参数
        if(a+b+c+d+e<20)g++;//如果他们的和小于20，g加1
        djy++;//相当for语句中的i
    }
    cout<<g<<endl;//输出结果并换行
        return 0;
    }

