using namespace std;
long long n;//定义n
long long a,b;//定义a,b
int main()
{
	scanf("%lld",&n);//输入n
	for(int i=1;i<=n;i++)
	{
		scanf("%lld%lld",&a,&b);//输入a,b
		if(a==-2147483647&&b==-1) cout<<"9223372036854775808"<<endl;//特判（记得换行）
		else cout<<a/b<<endl;//输出a/b（记得换行）
	}

	return 0;//结束“题解 AT786【整数除法】”
}
```
