#include<cstdio>
#include<algorithm>
#include<cmath>
#include<cstring>
#include<string>

using namespace std;
string st;
int main()
{
 for(int i=1;i<=8;i++) cin>>st[i];
 if(st[3]=='B') cout<<"Bachelor"<<" "; 
