{
    cin>>a;
    //判断身份
    if(a[2]=='B') cout<<"Bachelor"<<" ";//如果是学士课程的话输出Bachelor
    if(a[2]=='M') cout<<"Master"<<" ";//如果是硕士课程的话，输出Master
