#include<bits/stdc++.h>
using namespace std;
string s[6000],a;
int k[6000],m;
int main() {
	int n=0;
	while(cin>>a) {
		n++;
		s[n]=a;
	}
	for(int i=n;i>=3;i--) {
		if(s[i]!="not") {
			m=i-1;
			while(s[m]=="not"&&s[m-1]=="not") {
				k[m]=k[m-1]=1;
				m-=2;
			}
		}
	}
	m=n;
	while(k[m]==1) {
		m--;
	}
	for(int i=1;i<=n;i++) {
		if(k[i]==0) {
			cout<<s[i];
		if(i!=m)
			cout<<' ';
		}	
	}
	cout<<endl;
	return 0;
}
```

