```cpp
#include <iostream>
#include <string>
using namespace std;

int main()
{
	ios::sync_with_stdio(false);
	int n;
	cin >> n;
	for (register int i = 1; i <= n; i++)
	{
		string a, b, c;
		cin >> a >> b >> c;
		switch (a[0])
		{
		case 'B':
			cout << c[0];
			break;
		case 'M':
			cout << c[(c.length() - 1) >> 1];
			break;
		default:
			cout << c[c.length() - 1];
			break;
		}
	}
	cout << endl;
	return 0;
}
```

