    for(int i=1;i<=t;i++){
        int a=(i-1)/m+1,b=(i-1)%m+1;
        tmp[abs(a-x)+abs(b-y)]++;
    }
    for(int i=0;i<=t;i++)
        if(cnt[i]^tmp[i]) return 0;
    return 1;
}
void sol(){
    scanf("%d",&t);
    for(int i=1;i<=t;i++){
        scanf("%d",a+i);
        cnt[a[i]]++;
    }
    if(cnt[0]^1){
        puts("-1");
        return;
    }
    int x,dis=*max_element(a+1,a+t+1);
    for(int i=1;i<=t;i++)
        if(cnt[i]!=4*i){
            x=i;
            break;
        }
    for(int i=1;i<=t;i++){
        if(t%i) continue;
        int n=i,m=t/i,y=n+m-x-dis;
        memset(tmp,0,sizeof(tmp));
        if(chk(n,m,x,y)){
            printf("%d %d\n%d %d\n",n,m,x,y);
