#  $Code$ 

```cpp
#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
#define gc getchar
inline ll read(){
	ll x = 0; char ch = gc(); bool positive = 1;
	for (; !isdigit(ch); ch = gc())	if (ch == '-')	positive = 0;
	for (; isdigit(ch); ch = gc())	x = x * 10 + ch - '0';
	return positive ? x : -x;
}
int n,m,nl[200020],cnt[200020],id;
int mp[200020*2];
ll ans;
int main(){
	cin>>n>>m;
	for(int i=1;i<=n;i++){
		nl[i]=read();
		if(nl[i]==m) id=i;
		if(nl[i]>=m) cnt[i]=cnt[i-1]+1;
		else if(nl[i]<m) cnt[i]=cnt[i-1]-1;
	}
	for(int i=id;i<=n;i++){
		mp[cnt[i]+n]++;
	}
	for(int i=0;i<id;i++){
		ans+=mp[cnt[i]+n+1]+mp[cnt[i]+n+2];
	}
	cout<<ans<<endl;
	return 0;
}
```
