{
    re LL x=0,w=1;re char ch;
    while(ch<'0'||ch>'9') {if(ch=='-') w=-1;ch=getchar();}
    while(ch>='0'&&ch<='9') {x=(x<<3)+(x<<1)+(ch^48);ch=getchar();}
    return x*w;
}
LL n,a[N],an,ss,bb; //乱定变量名(逃

int main()
{
  n=rd();
  for(int i=1;i<=n;i++) a[i]=rd();
  for(int i=1;i<=n;i++)
    {
      bb=((bb<<1)%mod+a[i])%mod;
      an=(ss+bb)%mod;
      ss=(ss+an)%mod;
    }
  printf("%lld\n",an);
  return 0;
