#include<bits/stdc++.h>
#define fol(i,a,b) for(int i=a;i<=(b);i++)
#define bor(i,a,b) for(int i=a;i>=(b);--i)
using namespace std;
int main(){
    int n,k;
    ios::sync_with_stdio(false);
    cin.tie(0);
    string str;
    cin>>n>>k>>str;
    sort(str.begin(),str.end());
    int sum=str[0]-'a'+1;
    char tmp=str[0];
    int num=1;
    fol(i,1,n-1){
        if(num==k){
            break;
        }
        if(str[i]-tmp>1){
            num++;
            tmp=str[i];
            sum+=tmp-'a'+1;
        }
    }
    if(num==k){
        cout<<sum<<endl;
    }
    else{
        cout<<-1<<endl;
    }
    return 0;
}
