{
	freopen("matrix.in","r",stdin);
	freopen("matrix.out","w",stdout);
	n=read(),k=read();
	for(int i=1;i<=n;i++)
		f[i][i]=1;
	for(int i=2;i<=n;i++){
		for(int j=1;j<i;j++)
			for(int k=i-j;k<i;k++)
				f[i][j]=(f[i][j]+f[k][min(j,k)])%mod;
		f[i][i]+=f[i][i-1];
	}
	for(int i=n;i>=1;i--)
		f[n][i]=(f[n][i]-f[n][i-1]+mod)%mod;
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
			if(i*j<k)ans=(ans+f[n][i]*f[n][j])%mod;
	printf("%lld",(ans<<1)%mod);
