		std::cin >> a[i],
		b[a[i]] += 1;
	int A = 0, B = 0, C = 0;
	for (int i = 1; i <= M; i++) {
		if (b[i] == 1)
			A += 1;
		if (b[i] == 2)
			B += 1;
		if (b[i] > 2)
			C += 1;
	}
	if (A % 2 == 1 and C == 0)
		std::cout << "NO\n";
	else {
		std::cout << "YES\n";
		if (A % 2 == 0) {
			int Multiset = 0;
			for (int i = 1; i <= N; i++)
				if (b[a[i]] == 1)
					Ans[i] = Multiset + 'A',
					Multiset = 1 - Multiset;
				else Ans[i] = 'A';
		} else {
			int Multiset = 0;
			for (int i = 1; i <= N; i++)
				if (b[a[i]] == 1)
					Ans[i] = Multiset + 'A',
					Multiset = 1 - Multiset;
				else if (b[a[i]] == 2)
					Ans[i] = 'A';
			std::set<int> S;
			for (int i = 1; i <= N; i++)
				if (b[a[i]] > 2)
					S.insert(a[i]);
			int Stage = false;
			for (int i = 1; i <= N; i++)
				if (b[a[i]] > 2) 
					if (a[i] == *S.begin() and not Stage)
						Ans[i] = 'B',
						Stage = true;
					else
						Ans[i] = 'A';
		}
		for (int i = 1; i <= N; i++)
			std::cout << Ans[i] << (i == N ? "\n" : "");
	}
	return 0;
} 
```

