		if(cnt[i])
		{
			num++;
			if(cnt[i]==1)
				num1++;
			if(cnt[i]==2)
				num2++;
		}
	if((num1&1)==0)
	{
		cout<<"YES\n";
		for(ll i=1;i<=n;i++)
			if(cnt[s[i]]==1)
			{
				putchar(!f?'A':'B');
				f=!f;
			}
			else
				putchar('A');
	}
	else
	{
		if(num-num2==num1)
			cout<<"NO";
		else
		{
			cout<<"YES\n";
			for(ll i=1;i<=n;i++)
				if(cnt[s[i]]==1)
				{
					putchar(!f?'A':'B');
					f=!f;
				}
				else if(cnt[s[i]]==2)
					putchar('A');
				else if(!vis)
