int main() {
  cin >> n >> k;
  f[1][2][0] = f[1][3][1] = 2;//初始化
  for (int i = 2; i <= n; i++) {
    for (int j = 2; j <= k + 1; j++) {//注意这里要加1
      f[i][j][0] = (f[i - 1][j][1] * 2LL + f[i - 1][j][0] + f[i - 1][j - 1][0]) % mod;
      f[i][j][1] = (f[i - 1][j - 1][0] * 2LL + f[i - 1][j][1] + f[i - 1][j - 2][1]) % mod;
    }
  }
  cout << (f[n][k + 1][0] + f[n][k + 1][1]) % mod;
  return 0;//华丽的结束
}
