long long x,y,z,t1,t2,t3;
long long walk;
long long e;
int main()
{
    cin>>x>>y>>z>>t1>>t2>>t3;
    walk=abs(x-y)*t1;//计算走路所需时间
    e=abs((x-y))*t2+abs((x-z))*t2+3*t3;//计算电梯
