	int c = (a[j]>>i&1); // 当前这位是什么 
	if (!trie[old[j]][c]){
		tot++;
		old[j] = trie[old[j]][c] = tot;
	}
	else old[j] = trie[old[j]][c];
	sz[old[j]]++;
}
for (int j = 1; j <= n; j++){
	int c = (a[j]>>i&1);
	cnt += sz[trie[p[j]][c]];
} 
int pan = (K > cnt); // 取零还是取一
if (pan){
	K -= cnt;
	ans += (1ll<<i);
