#include<bits/stdc++.h>
#define TP int
using namespace std;
inline TP read(){
    char c=getchar();
    TP x=0;
    bool f=0;
    while(!isdigit(c))f^=!(c^45),c=getchar();
    while(isdigit(c))x=(x<<1)+(x<<3)+(c^48),c=getchar();
    if(f)x=-x;return x;
}
//以上为快读，帮你省时间，时间就是金钱，换句话说就是帮你省钱~~~
int n=read(),ans1,ans2;
bool Ehab(int n){
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			if(i%j==0){
            	//第一个条件就是：要满足a%b==0
                //如果连这个条件都不满足，那么就不用判断下一个条件了
				int s1=i*j,s2=i/j;
                //s1和s2是用来判断的
				if(s1>n&&s2<n){
                	//这是来判断 a⋅b>x  a/b<x 的
					ans1=i;
					ans2=j;
                    //如果可以把答案存入ans1,ans2中
					return true;
                  	//结束！
				}
			}
		}
	}
    return false;
    //如果没有找到正确答案就......
    //返回 false
}
int main(){
	if(Ehab(n)==false){
    	//如果没有找到就输出-1
		cout<<"-1";
	}
	else if(Ehab(n)==true){
    	//找到了就输出！
		cout<<ans1;
		cout<<" ";
		cout<<ans2;
	}
	return 0;
}
```
