	res = 0; memset(dp, 0, sizeof dp); dp[cnt] = 1;
	for (int i = 1; i <= k; ++i)
		for (int j = 0; j <= cnt; ++j) {
			(dp[j] *= inv[j + 1]) %= mod;
			for (int k = j + 1; k <= cnt; ++k)
				(dp[j] += dp[k] * inv[k + 1] % mod) %= mod;
		}
	ll bin = 1, tmp = val % mod;
	for (int i = 0; i <= cnt; ++i, (bin *= tmp) %= mod)
