{
    int ans=0;
    for(int i=0;i<m;i+=2)ans+=max(cow[i][a][0]+cow[i][b][1],cow[i][b][0]+cow[i][a][1]);
    for(int i=1;i<m;i+=2)ans+=max(cow[i][c][0]+cow[i][d][1],cow[i][d][0]+cow[i][c][1]);
    return ans;
}
