	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			if(a[i]==b[j]){
				for(int k=0;k<j;k++){
					if(b[k]<a[i]){
						dp[i][j]=max(dp[i][j],dp[i-1][k]+1);
					}
				}
				if(Max<dp[i][j]) Max=dp[i][j],x=i,y=j;
			}
			else{
				dp[i][j]=dp[i-1][j];
			}
		}
	}
	printf("%d\n",Max);
	print_ans(x,y);
	return 0;
}

