    cin>>q;
    for(int i = 1;i <= q;i++) cin>>l[i]>>r[i]>>d[i];
    for(int i = 1;i <= q;i++){
        if(d[i] < l[i]) cout<<d[i]<<endl;
        else cout<<(r[i] / d[i]) * d[i] + d[i]<<endl;
    }
    return 0;
}
