#include<cstdio>
int n,x,y,xa,ya;
char s[1];
int main(){
	for(scanf("%d",&n);n--;){
		scanf("%s%d%d",s,&x,&y);
		if(x>y)x^=y^=x^=y;
		if(s[0]=='+'){
			if(x>xa)xa=x;
			if(y>ya)ya=y;
		}
		else
			puts(xa<=x&&ya<=y?"YES":"NO");
	}
	return 0;
}
```

