			int l=i;
			for(int j=i+1;j<=n;j++){
				while(dp[i][l][k&1]<t[j]-t[l])	l++;
				dp[i][j][(k+1)&1]=min(dp[i][l][k&1],t[j]-t[l-1]);
//				cout<<i<<" "<<j<<" "<<k<<" "<<l<<" "<<dp[i][j][(k+1)&1]<<endl;
