       s[u][2]+=s[v][2];
    }
  }
  if(s[u][1]==0&&s[u][2]==sumb||s[u][2]==0&&s[u][1]==suma)
  {
     zs++;
  }
}

int main()
{
  cin>>n;
  for(int i=1;i<=n;i++)
  {
     cin>>a[i];
     if(a[i]==1)suma++;
     if(a[i]==2)sumb++;
  }
  for(int i=2;i<=n;i++)
  {
    cin>>x>>y;
    vec[x].push_back(y);
    vec[y].push_back(x);
  }
