				I += base*2;
			else if(p && !(i<<1>>p&1))
				I += base*2;
			else if((k/base)%3 == 0)
				I += base*2;
			else I += base;
			if((k/base)%3 == 1)
				cnt = -infty;
			else ++ cnt;
