bool check(int x){
	for(int i=0;i<n;i++){//判断是否铺满。
		if(x&(1<<i)) continue;
		return 1;
	}
	return 0;
}
signed main(){
	memset(dp,0x3f,sizeof(dp));
	cin>>n>>m;
	if(n>m) swap(n,m);
	for(int h=1;h<=m;h++){
		for(int i=0;i<(1<<n);i++){
			int tot=0;
			for(int j=0;j<n;j++) if(i&(1<<j)) tot++;
			dp[1][i][0]=tot;
		}
	}
	for(int h=2;h<=m;h++){
		for(int i=0;i<(1<<n);i++){
			int tot=0;
			for(int j=0;j<n;j++) if(i&(1<<j)) tot++;
			for(int j=0;j<(1<<n);j++){
